#program looks for file, if not found error will print
try:
    infile = open("temperature.txt" , "r")
except IOError:
    print("File not found")
#reads each line
line1 = infile.readline()
line2 = infile.readline()
line3 = infile.readline()
line4 = infile.readline()
line5 = infile.readline()
line6 = infile.readline()
line7 = infile.readline()
line8 = infile.readline()
line9 = infile.readline()
line10 = infile.readline()
line11 = infile.readline()
line12 = infile.readline() 
#splits the element in lines by space
jan = line1.split()
feb = line2.split()
mar = line3.split()
apr = line4.split()
may = line5.split()
jun = line6.split()
jul = line7.split()
aug = line8.split()
sep = line9.split()
octo = line10.split()
nov = line11.split()
dec = line12.split()
#adding temps to a list
temp = [["Jan Data:",jan],
        ["Feb Data:",feb],
        ["Mar Data:",mar],
        ["Apr Data:",apr],
        ["May Data:",may],
        ["Jun Data:",jun],
        ["Jul Data:",jul],
        ["Aug Data:",aug],
        ["Sep Data:",sep],
        ["Oct Data:",octo],
        ["Nov Data:",nov],
        ["Dec Data:",dec],]
        
        
print(temp)
print()
#using max function, the program is looking for higest temp in each line and stores it in list
print("Highest temp for each month")
newlist = ["Jan Data:", max(jan),
           "Feb Data:",max(feb),
           "Mar Data:",max(mar),
           "Apr Data:",max(apr),
           "May Data:",max (may),
           "Jun Data:",max (jun),
           "Jul Data:",max (jul),
           "Aug Data:",max (aug),
           "Sep Data:",max (sep),
           "Oct Data:",max (octo),
           "Nov Data:",max (nov),
           "Dec Data:",max (dec),]
        
print(newlist)
#writing highest temps in a text file
hightemp = str(newlist)
outfile = open("hightemp.txt","w")
outfile.write(hightemp)
outfile.close()
infile.close()

